import { AddLocation, Call, Email, Facebook, GitHub, Instagram, Twitter, WhatsApp } from "@material-ui/icons";
import styled from "styled-components"

const Container = styled.div`
    display: flex;
`;

const Left = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    padding: 20px;
`;

const Logo = styled.h1`
    letter-spacing: 2px;
    font-weight: bold;
    font-family: Poppins;
`;

const Desc = styled.p`
    margin: 20px 0px;
    line-height: 25px;
    word-spacing: 5px;
`;

const SocialContainer = styled.div`
    display: flex;
`;

const SocialIcon = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    color: white;
    background-color: #${props => props.color};
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 20px;
    cursor: pointer;
`;

const Center = styled.div`
    flex: 1;
    padding: 20px;
`;

const Title = styled.h2`
    margin-top: 10px;
    margin-bottom: 25px;
    letter-spacing: 1px;
    font-weight: bold;
    font-family: Poppins;
`;
const List = styled.ul`
    margin: 0;
    padding: 0;
    list-style: none;
    display: flex;
    flex-wrap: wrap;
`;

const ListItem = styled.li`
    width: 50%;
    margin-bottom: 5px;
`;

const Right = styled.div`
    flex: 1;
    padding: 20px;
`;
const ContactItem = styled.div`
`;

const Footer = () => {
  return (
    <Container>
      <Left>
          <Logo>Jean.</Logo>
          <Desc>
          In publishing and graphic design, Lorem 
          ipsum is a placeholder text commonly used to 
          demonstrate the visual form of a document or a typeface withou
          </Desc>
          <SocialContainer>
              <SocialIcon color = "3B5999">
                  <Facebook />
              </SocialIcon>
              <SocialIcon color = "55ACEE" >
                  <Twitter />
              </SocialIcon>
              <SocialIcon color = "E4405F">
                  <Instagram />
              </SocialIcon>
              <SocialIcon color = "E60023">
                  <GitHub />
              </SocialIcon>
              <SocialIcon color = "04F404">
                  <WhatsApp />
              </SocialIcon>
          </SocialContainer>
      </Left>

      <Center>
          <Title>Useful Links</Title>
          <List>
              <ListItem>Home</ListItem>
              <ListItem>Cart</ListItem>
              <ListItem>Man Fashion</ListItem>
              <ListItem>Woman Fashion</ListItem>
              <ListItem>Accessories</ListItem>
              <ListItem>Order Tracking</ListItem>
              <ListItem>WishList</ListItem>
              <ListItem>Terms</ListItem>
          </List>
      </Center>

      <Right>
          <Title>Cantact</Title>
          <ContactItem><AddLocation/> Kigali/Rwanda</ContactItem>
          <ContactItem><Call/> +250 781917267</ContactItem>
          <ContactItem><Email/> jeancyifuzodamas@gmail.com</ContactItem>
      </Right>
    </Container>
  )
}

export default Footer
