import { Facebook, GitHub, Instagram, Twitter, WhatsApp } from "@material-ui/icons";
import styled from "styled-components"

const Container = styled.div`
    display: flex;
`;

const Left = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    padding: 20px;
`;

const Logo = styled.h1``;

const Desc = styled.p`
    margin: 20px 0px;
    line-height: 25px;
    word-spacing: 5px;
`;

const SocialContainer = styled.div`
    display: flex;
`;

const SocialIcon = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    color: white;
    background-color: #${props => props.color};
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 20px;
    cursor: pointer;
`;

const Center = styled.div`
    flex: 1;
    padding: 20px;
`;

const Title = styled.h3``;
const List = styled.ul``;
const ListList = styled.li``;

const Right = styled.div`
    flex: 1;
    padding: 20px;
`;

const Footer = () => {
  return (
    <Container>
      <Left>
          <Logo>Jean.</Logo>
          <Desc>
          In publishing and graphic design, Lorem 
          ipsum is a placeholder text commonly used to 
          demonstrate the visual form of a document or a typeface withou
          </Desc>
          <SocialContainer>
              <SocialIcon color = "3B5999">
                  <Facebook />
              </SocialIcon>
              <SocialIcon color = "E4405F">
                  <Twitter />
              </SocialIcon>
              <SocialIcon color = "55ACEE">
                  <Instagram />
              </SocialIcon>
              <SocialIcon color = "E60023">
                  <GitHub />
              </SocialIcon>
              <SocialIcon color = "E60023">
                  <WhatsApp />
              </SocialIcon>
          </SocialContainer>
      </Left>
      <Center></Center>
      <Right></Right>
    </Container>
  )
}

export default Footer
